import GuessArea from './components/GuessArea.vue';
import Keyboard from './components/QuordleKeyboard.vue';
import HeadMain from './components/HeadMain.vue';
import WordListArea from './components/WordListArea.vue';
export default {
  components: {
    GuessArea,
    Keyboard,
    HeadMain,
    WordListArea
  },
  methods: {
    addTypedLetter(event) {
      console.log(event.key);
      if (this.$store.state.currentGuessLetters.length < 5) {
        this.$store.commit('ADD_LETTER', event.key);
      }
    }
  },
  mounted() {
    let self = this;
    window.addEventListener('keyup', function (ev) {
      self.addTypedLetter(ev); // declared in your component methods
    });
  }
};
