import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    currentGuessNumber: 1,
    currentGuessLetters: []

  },
  getters: {
  },
  mutations: {
    ADD_LETTER(state, letter){
      state.currentGuessLetters.push(letter);
    },
    REMOVE
  },
  actions: {
  },
  modules: {
  }
})
