import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    currentGuessNumber: 1,
    guessedAllWords: false,
    bombedOut: false,
    currentGuessLetters: ['A', 'B', 'C', 'D', 'E'],
    guesses: [
      ['','','','',''],
      ['','','','',''],
      ['','','','',''],
      ['','','','',''],
      ['','','','',''],
      ['','','','',''],
      ['','','','',''],
      ['','','','',''],
      ['','','','','']
    ]

  },
  getters: {
    updatedGuesses: state =>{
      if (!state.guessedAllWords && !state.bombedOut){
        const reactiveGuesses = state.guesses;
        const rowNumber = reactiveGuesses.findIndex(row => row.find(letter => letter === ''));
        reactiveGuesses[rowNumber] = state.currentGuessLetters;
        return state.guesses;
      }
    } 
  },
  mutations: {
    ADD_LETTER(state, letter){
      state.currentGuessLetters.push(letter);
    },
    REMOVE_LETTER(state){
      state.currentGuessLetters.pop();
    },
    RESET_CURRENT_LETTERS(state){
      state.currentGuessLetters = [];
    }
  },
  actions: {
  },
  modules: {
  }
})
