import GuessArea from './components/GuessArea.vue';
import Keyboard from './components/QuordleKeyboard.vue';
import MiddleMain from './components/MiddleMain.vue';
import WordListArea from './components/WordListArea.vue';
export default {
  components: {
    GuessArea,
    Keyboard,
    MiddleMain,
    WordListArea
  },
  methods: {
    addTypedLetter(event) {
      if (this.$store.state.currentGuessLetters.length < 5 && event.which > 64 && event.which < 91) {
        //65=A 90=Z
        this.$store.commit('ADD_LETTER', event.key.toUpperCase());
      } else if (event.which == 8) {
        //Backspace
        this.$store.commit('REMOVE_LETTER');
      } else if (event.which == 13) {
        //Enter
        event.stopPropagation();
        event.preventDefault();
        if (this.$store.state.guessable.includes(this.$store.state.currentGuessLetters.join(''))) {
          this.$store.commit('ENTER_WORD');
          this.$store.commit('RESET_CURRENT_LETTERS');
        }
      }
    }
  },
  mounted() {
    let self = this;
    window.addEventListener('keydown', function (event) {
      if (event.repeat) return;
      self.addTypedLetter(event);
    });
    this.$store.subscribe((mutation, state) => {
      localStorage.setItem('store', JSON.stringify(state));
    });
  }
};
